import React, { useEffect, useRef, useState } from "react";
import ms from "milsymbol";
import {
  Coding_Scheme,
  AffiliationData,
  StatusData,
  warFighting,
  TacticalGrapics,
  SignalIntelligence,
  stabilityOperation,
  emeergencyManagement,
} from "./constraints";

function App() {
  const [id, setId] = useState("");
  const [dataProcess, setDataProcess] = useState([]);
  const [functionunit, setFunctionunit] = useState([]);
  const [image, setImage] = useState(null);
  const [defaultfunction, setDefaultfunction] = useState("");
  const [scheme,setScheme] = useState("");
  const [affliation,setAffliation] = useState("");
  const [battel,setBattel] = useState("");
  const [status,setStatus] = useState("");
  const [functions, setFunctions] = useState("");
  const [modifier1,setModifier1] = useState("-");
  const [modifier2,setModifier2] = useState("-");
  // const [data, setData] = useState({
  //   scheme: "",
  //   affliation: "",
  //   battle: "",
  //   status: "",
  //   function: "",
  //   modifier1: "-",
  //   modifier2: "-",
  // });
  const refvalue = useRef();

  const handleinput = (e) => {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
    console.log("refvalue.current.value",refvalue?.current?.value)
  };
  const handleScheme=(value)=>{
 setScheme(value);
  }
  const handleAffliation=(value)=>{
setAffliation(value);

  }
  const handleBattel=(value)=>{
setBattel(value)
  }
  const handleStatus=(value)=>{
setStatus(value);
  }
  const handleFunction=(value)=>{
setFunctions(value);
  }


  useEffect(() => {
    console.log(data);
    switch (data.scheme) {
      case "S":
        setDataProcess(warFighting);
        break;
      case "G":
        setDataProcess(TacticalGrapics);
        break;
      case "I":
        setDataProcess(SignalIntelligence);
        break;
      case "O":
        setDataProcess(stabilityOperation);
        break;
      case "E":
        setDataProcess(emeergencyManagement);
        break;
      default:
        setDataProcess([]);
    }
    {
      let srt1 =
        data.scheme +
        data.affliation +
        data.battle +
        data.status +
        data.function;
      let length = srt1.length;
      let length2 = 10 - length;
      for (let i = 1; i <= length2; i++) {
        srt1 += "-";
      }

      let srt2 = srt1 + data.modifier1 + data.modifier2;
      setId(srt2);
      let a = new ms.Symbol(srt2, {
        size: 35,
      }).asSVG();
      setImage(a);
    }
  }, [data]);

  useEffect(() => {
    dataProcess?.map((item, i) => {
      if (item.value == data.battle) {
        setFunctionunit(item.children);
      }
    });



  }, [data?.battle]);



  return (
    <div className="App">
      <div>
        <h2>{id}</h2>
        <div dangerouslySetInnerHTML={{ __html: image }}></div>
      </div>
      <form>
        <label htmlFor="scheme">Coding Scheme:</label>
        <select name="scheme" onChange={() => handleScheme(value)}>
          <option value="">please select</option>
          {Coding_Scheme?.map((items, i) => {
            return (
              <option value={items?.value} id={items?.id}>
                {items?.name}
              </option>
            );
          })}
        </select>
        <br />
        <br />
        <label htmlFor="affliation">Affiliation:</label>
        <select
          name="affliation"
          id="affliation"
          onChange={() => handleAffliation(value)}
        >
          <option value="">please select</option>
          {AffiliationData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="battle">Battle Dimension:</label>
        <select
          name="battle"
          id="battle"
          value={data?.battle}
          onChange={() => handleBattel(value)}
        >
          <option value="">please select</option>

          {dataProcess.map((item) => (
            <option value={item?.value}>{item?.name}</option>
          ))}
        </select>
        <br />
        <br />
        <label htmlFor="status">Status:</label>
        <select name="status" id="status" onChange={() => handleStatus(value)}>
          <option value="">please select</option>

          {StatusData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="function">Function ID:</label>
        <select
          name="function"
          id="function"
          value={data?.function}
          selected
          ref={refvalue}
          onChange={() => handlefunction(value)}
        >
          <option value="">please select</option>
          {functionunit.map((item, index) => (
            <option value={item?.value}>{item?.name}</option>
          ))}
        </select>
        <br />
        <br />
      </form>
    </div>
  );
}

export default App;
