import React, { useEffect, useState } from "react";
import ms from "milsymbol";
import {
  Coding_Scheme,
  AffiliationData,
  StatusData,
  warFighting,
  TacticalGrapics,
  SignalIntelligence,
  stabilityOperation,
  emeergencyManagement,
} from "./constraints";

function App() {
  const [id, setId] = useState("");
  const [dataProcess, setDataProcess] = useState([]);
  const [functionunit, setFunctionunit] = useState([]);
  const [image, setImage] = useState(null);
  const [war, setWar] = useState([]);
  const [data, setData] = useState({
    scheme: "",
    affliation: "",
    battle: "",
    status: "",
    function: "",
    modifier1: "-",
    modifier2: "-",
  });

  // const managestate = (e) => {
  // //   e.preventDefault();
  // //   console.log(data);

  // //   let srt1 =
  // //     data.scheme + data.affliation + data.battle + data.status + data.function;

  // //   let srt2 = srt1 + data.modifier1 + data.modifier2;
  // //   setId(srt2);
  // //   let a = new ms.Symbol(srt2, {
  // //     size: 35,
  // //   }).asSVG();
  // //   setImage(a);
  // };

  const handleinput = (e) => {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  };
  useEffect(() => {

    switch (data.scheme) {
      case "S":
        setDataProcess(warFighting);

        break;
      case "G":
        setDataProcess(TacticalGrapics);
        break;
        case "I":
          setDataProcess(SignalIntelligence);
         break;
        case "O":
          setDataProcess(stabilityOperation);
          break;
        case "E":
          setDataProcess(emeergencyManagement);
          break;
      default:
        setDataProcess([]);
    }
    {
      dataProcess.map((item, i) => {
        if (item.value == data.battle) {
          setFunctionunit(item.children);
        }
      });
    }
    {
    let srt1 =
    data.scheme + data.affliation + data.battle + data.status + data.function;
    let length = srt1.length;
    let length2 = 10 - length;
    for (let i = 1; i <= length2; i++) {
      srt1 += "-";
    }

  let srt2 = srt1 + data.modifier1 + data.modifier2;
  setId(srt2);
  let a = new ms.Symbol(srt2, {
    size: 35,
  }).asSVG();
  setImage(a);
}
  }, [data]);

  useEffect(() => {
    console.log("warfighting data", dataProcess);
  });

  return (
    <div className="App">
      <div>
        <h2>{id}</h2>
        <div dangerouslySetInnerHTML={{ __html: image }}></div>
      </div>
      <form>
        <label htmlFor="scheme">Coding Scheme:</label>
        <select name="scheme" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>
          {Coding_Scheme?.map((items,i) => {
            return (
              <option value={items?.value} id={items?.id} >
                {items?.name}
              </option>
            );
          })}
        </select>
        <br />
        <br />
        <label htmlFor="affliation">Affiliation:</label>
        <select
          name="affliation"
          id="affliation"
          onChange={(e) => handleinput(e)}
        >
          <option value="">please select</option>
          {AffiliationData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="battle">Battle Dimension:</label>
        <select name="battle" id="battle" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {dataProcess.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="status">Status:</label>
        <select name="status" id="status" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {StatusData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="function">Function ID:</label>

          <option value="">please select</option>

          {functionunit.map((item) => {
             <select name="function" id="function" onChange={(e) => handleinput(e)}>
             <option value={item?.value} >{item?.name}</option>;
            </select>
          })}

        <br />
        <br />

        {/* <input type="button" value="Submit" onClick={(e) => managestate(e)} /> */}

      </form>
    </div>
  );
}

export default App;
