import React, { useEffect, useState } from "react";
import {
  Coding_Scheme,
  AffiliationData,
  StatusData,
  warFighting,
} from "./constraints";

function App() {
  const [id, setId] = useState("");
  const [dataProcess,setDataProcess] = useState([])
  const [functionunit, setFunctionunit] = useState([])
  const [war, setWar] = useState([]);
  const [data, setData] = useState({
    scheme: "",
    affliation: "",
    battle: "",
    status: "P",
    function: "U",
    modifier1: "-",
    modifier2: "-",
  });

  const managestate = (e) => {
    e.preventDefault();
    console.log(data);

    let srt1 =
      data.scheme + data.affliation + data.battle + data.status + data.function;

    let srt2 = srt1 + data.modifier1 + data.modifier2;
    setId(srt2);
  };

  const handleinput = (e) => {
    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  };
  useEffect(()=>{
    console.log("warfighting",data.scheme)
    switch(data.scheme){
      case "S":
           setDataProcess(warFighting);
           console.log("warfighting",data.scheme)
           break;
      default:
         setDataProcess(warFighting);
    }
    let lastestarray = warFighting;
    let selectedIndex = warFighting.indexOf(data.battle);
    console.log("datagetting",warFighting[selectedIndex]);
      setFunctionunit(lastestarray[selectedIndex].children);

    console.log("datagetting",lastestarray[selectedIndex]);
  //  {
  //   dataProcess.map((item,i)=>{
  //     if (item.value==data.battle){
  //       setFunctionunit(item.children);
  //     }
  //   })
  //  }

  },[data]);

  useEffect(() => {
    console.log('warfighting data', dataProcess)

  });


  return (
    <div className="App">
      <div>
        <h2>{id}</h2>
      </div>
      <form>
        <label htmlFor="scheme">Coding Scheme:</label>
        <select name="scheme" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>
          {Coding_Scheme?.map((items) => {
            return (
              <option value={items?.value} id={items?.id}>
                {items?.name}
              </option>
            );
          })}
        </select>
        <br />
        <br />
        <label htmlFor="affliation">Affiliation:</label>
        <select
          name="affliation"
          id="affliation"
          onChange={(e) => handleinput(e)}
        >
          <option value="">please select</option>
          {AffiliationData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="battle">Battle Dimension:</label>
        <select name="battle" id="battle" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {dataProcess.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="status">Status:</label>
        <select name="status" id="status" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {StatusData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="function">Function ID:</label>
        <select name="function" id="function" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {functionunit.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />

        <input type="button" value="Submit" onClick={(e) => managestate(e)} />
      </form>
    </div>
  );
}

export default App;
