import React, { useEffect, useState } from "react";
import {
  Coding_Scheme,
  AffiliationData,
  BattleData,
  StatusData,
  FunctionData,
  Modifier1Data,
  Modifier2Data,
  warFighting,
} from "./content";

function App() {
  const [id, setId] = useState("");
  const [war, setWar] = useState([]);
  const [data, setData] = useState({
    scheme: "S",
    affliation: "F",
    battle: "G",
    status: "P",
    function: "U",
    modifier1: "-",
    modifier2: "-",
  });

  const managestate = (e) => {
    e.preventDefault();
    console.log(data);

    let srt1 =
      data.scheme + data.affliation + data.battle + data.status + data.function;
    let length = srt1.length;
    let length2 = 10 - length;
    for (let i = 1; i <= length2; i++) {
      srt1 += "-";
    }
    let srt2 = srt1 + data.modifier1 + data.modifier2;
    setId(srt2);
  };

  const handleinput = (e) => {
    console.log("e.target.name", e.target);

    setData({
      ...data,
      [e.target.name]: e.target.value,
    });
  };

  useEffect(() => {
    console.log("data", data);
    console.log("AffiliationData", AffiliationData);
    let obj = warFighting;

    let array = obj[0].mainicon[0].name;
    let last = array[array.length - 1];
    console.log("object array", obj[0]);
    console.log("main icon of first", obj[0].mainicon);
    console.log("war", last);
    console.log("war", last);
    setWar(obj);
  });


  return (
    <div className="App">
      <div>
        <h2>{id}</h2>
      </div>
      <form>
        <label htmlFor="scheme">Coding Scheme:</label>
        <select name="scheme" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>
          {Coding_Scheme?.map((items) => {
            return (
              <option value={items?.value} name={items?.name} id={items?.id}>
                {items?.name}
              </option>
            );
          })}
        </select>
        <br />
        <br />
        <label htmlFor="affliation">Affiliation:</label>
        <select
          name="affliation"
          id="affliation"
          onChange={(e) => handleinput(e)}
        >
          <option value="">please select</option>
          {AffiliationData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="battle">Battle Dimension:</label>
        <select name="battle" id="battle" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {BattleData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="status">Status:</label>
        <select name="status" id="status" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {StatusData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />
        <label htmlFor="function">Function ID:</label>
        <select name="function" id="function" onChange={(e) => handleinput(e)}>
          <option value="">please select</option>

          {FunctionData.map((item) => {
            return <option value={item?.value}>{item?.name}</option>;
          })}
        </select>
        <br />
        <br />

        <input type="button" value="Submit" onClick={(e) => managestate(e)} />
      </form>
    </div>
  );
}

export default App;
